#===============================
# Optimized Base64 Image Decoder (with Auto-Extension)
#===============================

function Get-ImageExtensionFromBytes {
    param (
        [byte[]]$Bytes
    )

    # Get first few bytes as Hex string
    $hexString = ($Bytes[0..7] | ForEach-Object { $_.ToString("X2") }) -join ""

    switch -regex ($hexString) {
        '^FFD8'       { return ".jpg" }
        '^89504E47'   { return ".png" }
        '^47494638'   { return ".gif" }
        '^424D'       { return ".bmp" }
        '^52494646'   { return ".webp" } # WEBP or others based on further bytes
        default       { return ".bin" } # Unknown type
    }
}

#=======================================================
# Generate Base64 image
#=======================================================

function Save-Base64Image {
    param (
        [string]$Base64String,
        [string]$OutputFolder,
        [string]$BaseName = "Image"
    )

    try {
        # Decode Base64
        $imageBytes = [System.Convert]::FromBase64String($Base64String)

        # Detect extension
        $extension = Get-ImageExtensionFromBytes -Bytes $imageBytes

        # Build final output path
        $outputPath = Join-Path $OutputFolder ("$BaseName$extension")

        # Write with buffering
        $bufferSize = 8KB
        $fileStream = [System.IO.File]::Open($outputPath, [System.IO.FileMode]::Create, [System.IO.FileAccess]::Write, [System.IO.FileShare]::None)
        $bufferedStream = New-Object System.IO.BufferedStream($fileStream, $bufferSize)

        try {
            $bufferedStream.Write($imageBytes, 0, $imageBytes.Length)
        }
        finally {
            $bufferedStream.Close()
            $fileStream.Close()
        }

        Write-Output "Image saved successfully: $outputPath"
    }
    catch {
        Write-Error "Failed to save image: $_"
    }
}

# ==========================
# Detect Hostname and Domain
# ==========================

$hostname = $env:COMPUTERNAME
$system = Get-WmiObject Win32_ComputerSystem
$domain = $system.Domain
$isDomainJoined = $system.PartOfDomain

# ==========================
# Dynamic Company Config
# ==========================

# Dynamic company config
$CompanyConfig = @(
    @{ Name = "GCPL"; Domains = @("gainwellindia.com");       HostnamePatterns = @("GCPL", "ASPL") }
)

# ==========================
# Company Detection Logic
# ==========================

# Company detection logic
$company = ""

# STEP 1: Prioritize hostname matching
foreach ($config in $CompanyConfig) {
    foreach ($pattern in $config.HostnamePatterns) {
        if ($hostname -match $pattern) {
            $company = $config.Name
            break
        }
    }
    if ($company) { break }
}

# STEP 2: Fallback to domain if hostname didn't match
if (-not $company -and $isDomainJoined) {
    foreach ($config in $CompanyConfig) {
        foreach ($knownDomain in $config.Domains) {
            if ($knownDomain.ToLower() -eq $domain.ToLower()) {
                $company = $config.Name
                break
            }
        }
        if ($company) { break }
    }
}

